(function ($) {
    $.fn.cloudTag = function (options) {
        $.fn.cloudTag.defaults = {
            radius: 120,
            dtr: Math.PI/200,
            d: 200,
            mcList: [],
            active: false,
            lasta: 1,
            lastb: 1,
            distr: true,
            tspeed: 2,
            size: 250,
            mouseX: 0,
            mouseY: 0,
            howElliptical: 1,
            sa:null,
            ca:null,
            sb:null,
            cb:null,
            sc:null,
            cc:null

        };

        return this.each(function () {
            var opts = $.extend({},$.fn.cloudTag.defaults,options),
                $this = $(this),
                oDiv = document.getElementById('keyword-list'),
                aA = oDiv.getElementsByTagName('a'),
                mcList = [];

            var clear = function () {
                opts.dtr = Math.PI/200;

                mcList = [];
                opts.active = false;
                opts.lasta = 1;
                opts.lastb = 1;

                opts.mouseX=0;
                opts.mouseY=0;
            };

            var sineCosine = function ( a, b, c){

                opts.sa = Math.sin(a * opts.dtr);
                opts.ca = Math.cos(a * opts.dtr);
                opts.sb = Math.sin(b * opts.dtr);
                opts.cb = Math.cos(b * opts.dtr);
                opts.sc = Math.sin(c * opts.dtr);
                opts.cc = Math.cos(c * opts.dtr);
            };

            var positionAll = function(){
                var phi= 0;
                var theta= 0;
                var max= mcList.length;
                var i= 0;


                var aTmp=[];
                var oFragment=document.createDocumentFragment();

                //随机排序
                for(i=0;i<aA.length;i++)
                {
                    aTmp.push(aA[i]);
                }

                aTmp.sort
                (
                    function ()
                    {
                        return Math.random()<0.5?1:-1;
                    }
                );

                for(i=0;i<aTmp.length;i++)
                {
                    oFragment.appendChild(aTmp[i]);
                }

                oDiv.appendChild(oFragment);

                for( var i=1; i<max+1; i++){
                    if( opts.distr )
                    {
                        phi = Math.acos(-1+(2*i-1)/max);
                        theta = Math.sqrt(max*Math.PI)*phi;
                    }
                    else
                    {
                        phi = Math.random()*(Math.PI);
                        theta = Math.random()*(2*Math.PI);
                    }
                    //坐标变换
                    mcList[i-1].cx = opts.radius * Math.cos(theta)*Math.sin(phi);
                    mcList[i-1].cy = opts.radius * Math.sin(theta)*Math.sin(phi);
                    mcList[i-1].cz = opts.radius * Math.cos(phi);

                    aA[i-1].style.left=mcList[i-1].cx+oDiv.offsetWidth/2-mcList[i-1].offsetWidth/2+'px';
                    aA[i-1].style.top=mcList[i-1].cy+oDiv.offsetHeight/2-mcList[i-1].offsetHeight/2+'px';
                }
            };

            var doPosition = function () {

                var l=oDiv.offsetWidth/2;
                var t=oDiv.offsetHeight/2;
                for(var i=0;i<mcList.length;i++)
                {
                    aA[i].style.left=mcList[i].cx+l-mcList[i].offsetWidth/2+'px';
                    aA[i].style.top=mcList[i].cy+t-mcList[i].offsetHeight/2+'px';

                    aA[i].style.fontSize=Math.ceil(12*mcList[i].scale/2)+8+'px';

                    aA[i].style.filter="alpha(opacity="+100*mcList[i].alpha+")";
                    aA[i].style.opacity=mcList[i].alpha;
                }
            };

            var depthSort = function () {
                var i=0;
                var aTmp=[];

                for(i=0;i<aA.length;i++)
                {
                    aTmp.push(aA[i]);
                }

                aTmp.sort
                (
                    function (vItem1, vItem2)
                    {
                        if(vItem1.cz>vItem2.cz)
                        {
                            return -1;
                        }
                        else if(vItem1.cz<vItem2.cz)
                        {
                            return 1;
                        }
                        else
                        {
                            return 0;
                        }
                    }
                );

                for(i=0;i<aTmp.length;i++)
                {
                    aTmp[i].style.zIndex=i;
                }
            }

            var update = function() {
                var a;
                var b;
                if(opts.active)
                {
                    a = (-Math.min( Math.max( -opts.mouseY, -opts.size ), opts.size ) / opts.radius ) * opts.tspeed;
                    b = (Math.min( Math.max( -opts.mouseX, -opts.size ), opts.size ) / opts.radius ) * opts.tspeed;
                }
                else
                {
                    a = opts.lasta * 0.98;
                    b = opts.lastb * 0.98;
                }

                opts.lasta=a;
                opts.lastb=b;

                if(Math.abs(a)<=0.01 && Math.abs(b)<=0.01)
                {
                    return;
                }

                var c=0;
                sineCosine(a,b,c);
                for(var j=0;j<mcList.length;j++)
                {
                    var rx1=mcList[j].cx;
                    var ry1=mcList[j].cy*opts.ca+mcList[j].cz*(-opts.sa);
                    var rz1=mcList[j].cy*opts.sa+mcList[j].cz*opts.ca;

                    var rx2=rx1*opts.cb+rz1*opts.sb;
                    var ry2=ry1;
                    var rz2=rx1*(-opts.sb)+rz1*opts.cb;

                    var rx3=rx2*opts.cc+ry2*(-opts.sc);
                    var ry3=rx2*opts.sc+ry2*opts.cc;
                    var rz3=rz2;

                    mcList[j].cx=rx3;
                    mcList[j].cy=ry3;
                    mcList[j].cz=rz3;

                    opts.per=opts.d/(opts.d+rz3);

                    mcList[j].x=(opts.howElliptical*rx3*opts.per)-(opts.howElliptical*2);
                    mcList[j].y=ry3*opts.per;
                    mcList[j].scale=opts.per;
                    mcList[j].alpha=opts.per;

                    mcList[j].alpha=(mcList[j].alpha-0.6)*(10/6);
                }

                doPosition();
                depthSort();
            };


            var init = function () {
                var i=0;
                var oTag=null;


                // var oDiv = opts.oDiv,
                //     aA = opts.aA;

                for(i=0;i<aA.length;i++)
                {
                    oTag={};

                    oTag.offsetWidth=aA[i].offsetWidth;
                    oTag.offsetHeight=aA[i].offsetHeight;

                    mcList.push(oTag);
                }

                sineCosine( 0,0,0 );

                positionAll();

                oDiv.onmouseover=function ()
                {
                    opts.active=true;

                };

                oDiv.onmouseout=function ()
                {
                    opts.active=false;

                };

                oDiv.onmousemove=function (ev)
                {
                    var oEvent=window.event || ev;

                    opts.mouseX=oEvent.clientX-(oDiv.offsetLeft+oDiv.offsetWidth/2);
                    opts.mouseY=oEvent.clientY-(oDiv.offsetTop+oDiv.offsetHeight/2);

                    opts.mouseX/=5;
                    opts.mouseY/=5;

                };

                $("#keyword-list a").hover(function () {
                    opts.active = false;
                    opts.dtr = Math.PI/1500;

                },function () {
                    opts.active = true;
                    opts.dtr = Math.PI/200;
                });


                var intervalTag = $this.data("intervalTag");
                clearInterval(intervalTag);
                var inter = setInterval(update, 30);
                $this.data("intervalTag",inter);
            }

            init();

        })

    }


})(jQuery);